mapper.update:
update t_apply_dog set last_update_date=now()
 , id=#{vo.id}
 , work_unit=#{vo.workUnit}
 , apply_amount=#{vo.applyAmount}
 , dog_id=#{vo.dogId}
 , police_man=#{vo.policeMan}
 , police_id=#{vo.policeId}
 , apply_state=#{vo.applyState}
 , apply_desc=#{vo.applyDesc}
 , approve_detail=#{vo.approveDetail}
 , creation_date=#{vo.creationDate}
 , last_update_date=#{vo.lastUpdateDate}
where id=#{vo.id}
